  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(255) NOT NULL,
  `slug` varchar(255) NOT NULL,
  `genre` varchar(255) NOT NULL,
  `pays_origine` varchar(255) DEFAULT NULL,
  `presentation` longtext NOT NULL,
